    private void RespActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_RespActionPerformed
        Double resultado;
        String expresion = Screen.getText(); //Obtenemos la expresion infija que el usuario quiere evaluar
        boolean f=true;
        try {
            
            int i=0;   
            while(i<expresion.length() && f==true){
                if(expresion.charAt(i)== '+' || expresion.charAt(i)== '-' || expresion.charAt(i)== '/' || expresion.charAt(i)== '*')
                    f=false;
                i++;
            }
            
            if(f)
                Screen.setText(expresion);
            else{
               FuncionalidadCalculadora.verificarExpresion(expresion);
            expresion = FuncionalidadCalculadora.infijaAPostfija(expresion);
            resultado = FuncionalidadCalculadora.evaluarPostfija(expresion);
            Screen.setText(resultado.toString()); 
            }
             
        } catch(Exception e) {
            this.Screen.setText("Error");
        }
    }//GEN-LAST:event_RespActionPerformed

