    private void NegativoActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_NegativoActionPerformed
        borrarError();
        this.Screen.setText(this.Screen.getText()+"-");
    }//GEN-LAST:event_NegativoActionPerformed

    private void RestaActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_RestaActionPerformed
        StringBuilder cad = new StringBuilder();
        borrarError();
        cad.append(Screen.getText() + " - ");
        Screen.setText(cad.toString());
    }//GEN-LAST:event_RestaActionPerformed

    private void MultiActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_MultiActionPerformed
        StringBuilder cad = new StringBuilder();
        borrarError();
        cad.append(Screen.getText() + " * ");
        Screen.setText(cad.toString());
    }//GEN-LAST:event_MultiActionPerformed

    
    private void borrarError() {
        if(FuncionalidadCalculadora.verificarSiDiceError(Screen.getText()))
            Screen.setText("");
    }
    
